import React from 'react';

export function SurveyQuestion(props) {
    let questionList = [];
    for (let i = 0; i < Object.keys(props.questions).length; i++) {
        let question = props.questions[i];
        let idName = "Q" + (i + 1);
        questionList.push(<label id={idName} htmlFor={idName} key={idName}>{question}</label>);
        questionList.push(<RenderOptions key={i} idName={idName} options={props.options[i]} answer={props.answer} setAnswer={props.setAnswer} index={i}/>);
    }
    return questionList;
}

function RenderOptions(props) {
    const handleSelect = (event, answer, setAnswer, index) => {
        answer[index].A = event.target.value;
        setAnswer(answer);
    };

    return (
        <div className="col-auto">
            <select id={props.idName} className='d-inline-flex p-1' onClick={event => handleSelect(event, props.answer, props.setAnswer, props.index)}>
                <option defaultValue>---</option>
                <EachOption options={props.options} />
            </select>
        </div>
    )
}

function EachOption(props) {
    let optionList = [];
    for (let i = 0; i < Object.keys(props.options).length; i++) {
        const eachOption = props.options[i];
        let value = i + 1;
        optionList.push(<option key={i} value={value}>{eachOption}</option>)
    }
    return optionList;
}
