import React from 'react';
import { useState } from 'react';
import { CompareChart } from './CompareChart.js'
import { Survey } from './Survey.js';
import { CollegeCard } from './CollegeCard.js';
import { Route } from 'react-router-dom';
import { Routes } from 'react-router-dom';
import { YourCollege } from './YourCollege.js';
import { Link } from 'react-router-dom';


// The props should pass everything we need
// including the question data, the colleges information, and the selected colleges.
function App(props) {
  let collegeList = props.props.college;
  let options = props.props.options;

  // For save the selected colleges, set score 2 as default selected college, which is
  const [selectedColleges, setSelectedColleges] = useState(['University of Arizona']);
  let addSelectedCollege = (college) => {
    if (!selectedColleges.includes(college)) {
      setSelectedColleges([...selectedColleges, college]);
    }
  }

  // return multiple selected college cards
  let returnSelectedColleges = selectedColleges.map(college => {
    <CollegeCard key={college} college={college} />
    return null;
  });

  return (

    <main className="container-fluid">
      <header className="nav-list">
        <img className="logo" src="img/logo.png" alt="website logo" />
        <nav className="navbar nav-bar-light">
          <ul className="oversight-links">
            <Link to="/survey">Survey</Link>
            <Link to="/yourColleg">Your College</Link>
            <Link to="/compareChart">Compare Chart</Link>
          </ul>
        </nav>
      </header>
      <div className="container">
      <Routes>
        <Route path="survey" element={<Survey options={options} college={collegeList} addSelectedCollege={addSelectedCollege} />} />
        <Route path="yourCollege" element={<YourCollege />} />
        <Route path="compareChart" element={<CompareChart selectedColleges={selectedColleges} totalCollegeList={collegeList} />} />
      </Routes>
      </div>
    </main>
  );
}



export default App;
