import React, { useState } from 'react';
import { Results } from './Results';

export function Survey(props) {
    console.log(props);
    return (
        <EachQuestion options={props.options} college={props.college} />
    );
}

function EachQuestion(props) {
    const [answer1, setAnswer1] = useState(0);
    const [answer2, setAnswer2] = useState(0);
    const [answer3, setAnswer3] = useState(0);
    const [answer4, setAnswer4] = useState(0);
    const [answer5, setAnswer5] = useState(0);
    const [answer6, setAnswer6] = useState(0);
    const [answer7, setAnswer7] = useState(0);

    const [totalScore, setTotalScore] = useState(0);

    const handleSum = () => {
        let sumUp = parseInt(answer1) + parseInt(answer2) + parseInt(answer3) + parseInt(answer4) + parseInt(answer5) + parseInt(answer6) + parseInt(answer7)
        setTotalScore(sumUp);
    };

    const handleSelect1 = function (event) {
        let num = event.target.value
        setAnswer1(num)
    };

    const handleSelect2 = function (event) {
        let num = event.target.value
        setAnswer2(num)
    };

    const handleSelect3 = function (event) {
        let num = event.target.value
        setAnswer3(num)
    };

    const handleSelect4 = function (event) {
        let num = event.target.value
        setAnswer4(num)
    };

    const handleSelect5 = function (event) {
        let num = event.target.value
        setAnswer5(num)
    };

    const handleSelect6 = function (event) {
        let num = event.target.value
        setAnswer6(num)
    };

    const handleSelect7 = function (event) {
        let num = event.target.value
        setAnswer7(num)
    };

    const qs1 = props.options.map((eachOne, index) =>
        <option key={index} value={index}>{eachOne.option1}</option>);

    const qs2 = props.options.map((eachOne, index) =>
        <option key={index} value={index}>{eachOne.option2}</option>);

    const qs3 = props.options.map((eachOne, index) =>
        <option key={index} value={index}>{eachOne.option3}</option>);

    const qs4 = props.options.map((eachOne, index) =>
        <option key={index} value={index}>{eachOne.option4}</option>);

    const qs5 = props.options.map((eachOne, index) =>
        <option key={index} value={index}>{eachOne.option5}</option>);

    const qs6 = props.options.map((eachOne, index) =>
        <option key={index} value={index}>{eachOne.option6}</option>);

    const qs7 = props.options.map((eachOne, index) =>
        <option key={index} value={index}>{eachOne.option7}</option>);

    return (
        <div className="survey">
            <div className="form-select">
            <h2 className="title">Finding Your Best Fit Colleges!</h2>
            <p className="description">
                This is our survey that will help you decide what colleges will
                best fit your needs to achieve your dreams. College is important
                time in your life. However, what is more important, is ensuring
                you are at a University that equipts you with the necessary
                tools to suceed.
                <em> Disclaimer we will ask personal questions regarding your
                    location, financial needs, career aspiration. If you dont want
                    to share this information please feel free not to take the
                    survey.</em>
            </p>
            <label id='qs1' for='Q1' className="qs1">* Where would you prefer your university to be located?</label>
            <div className="col-auto">
                <select id='Q1' className='d-inline-flex p-1' onChange={handleSelect1}>
                    <option selected="selected">---</option>
                    {qs1}
                </select>
                <button type="submit" className="btn btn-sm btn-success m-2">Save!</button>
            </div>
            <label id='qs2' for='Q2' className="qs2 pt-3">* What industry do you want your school to be surrounded by?</label>
            <div className="col-auto">
                <select id='Q2' className='d-inline-flex p-1' onChange={handleSelect2}>
                    <option selected="selected">---</option>
                    {qs2}
                </select>
                <button id="saveButton" type="submit" className="btn btn-sm btn-success m-2">Save!</button>
            </div>
            <label id='qs3' for='Q3' className="qs3 pt-3">* What setting do you want your school to be in?</label>
            <div className="col-auto">
                <select id='Q3' className='d-inline-flex p-1' onChange={handleSelect3}>
                    <option selected="selected">---</option>
                    {qs3}
                </select>
                <button id="saveButton" type="submit" className="btn btn-sm btn-success m-2">Save!</button>
            </div>
            <label id='qs4' for='Q4' className="qs4 pt-3">* What is your perfered college size?</label>
            <div className="col-auto">
                <select id='Q4' className='d-inline-flex p-1' onChange={handleSelect4}>
                    <option selected="selected">---</option>
                    {qs4}
                </select>
                <button id="saveButton" type="submit" className="btn btn-sm btn-success m-2">Save!</button>
            </div>
            <label id='qs5' for='Q5' className="qs5 pt-3">* How important is it for you to have good Financial Aid options?</label>
            <div className="col-auto">
                <select id='Q5' className='d-inline-flex p-1' onChange={handleSelect5}>
                    <option selected="selected">---</option>
                    {qs5}
                </select>
                <button id="saveButton" type="submit" className="btn btn-sm btn-success m-2">Save!</button>
            </div>
            <label id='qs6' for='Q6' className="qs6 pt-3">* Rate your importance in going to the most accredited college.</label>
            <div className="col-auto">
                <select id='qs6' className='d-inline-flex p-1' onChange={handleSelect6}>
                    <option selected="selected">---</option>
                    {qs6}
                </select>
                <button id="saveButton" type="submit" className="btn btn-sm btn-success m-2">Save!</button>
            </div>
            <label id='qs7' for='Q7' className="qs7 pt-3">* What type of University do you perfer?</label>
            <div className="col-auto">
                <select id='Q7' className='d-inline-flex p-1' onChange={handleSelect7}>
                    <option selected="selected">---</option>
                    {qs7}
                </select>
                <button id="saveButton" type="submit" className="btn btn-sm btn-success m-2">Save!</button>
            </div>
            <p><em>Click a submit button twice to successfully submit your answers.</em></p>
        </div>
            <div className="col-auto">
                <button id="saveButton" type="submit" className="btn btn-sm btn-primary m-2" onClick={handleSum}>Submit</button>
            </div>
            <div>
                <Results college={props.college} score={totalScore.toString()} addSelectedCollege={props.addSelectedCollege} />
            </div>
        </div>
    );
}
